$(window).scroll(function () {
  $(this).scrollTop() > 1
    ? $("header").addClass("sticky")
    : $("header").removeClass("sticky");
}),
  new WOW().init(),
  $(document).ready(function () {
    $(".testimonialCarousel").slick({
      dots: !0,
      infinite: !0,
      autoplay: !0,
      speed: 3e3,
      slidesToShow: 2,
      slidesToScroll: 1,
      arrows: !1,
      responsive: [{ breakpoint: 991, settings: { slidesToShow: 1 } }],
    });
  }),
  $(document).ready(function () {
    $(".indLedrCarousel").slick({
      dots: !0,
      infinite: !0,
      autoplay: !0,
      speed: 3e3,
      slidesToShow: 6,
      slidesToScroll: 1,
      arrows: !1,
      responsive: [
        { breakpoint: 1199, settings: { slidesToShow: 5 } },
        { breakpoint: 991, settings: { slidesToShow: 4 } },
        { breakpoint: 768, settings: { slidesToShow: 3 } },
        { breakpoint: 576, settings: { slidesToShow: 2 } },
      ],
    });
  }),
  $(".forbottom").click(function () {
    $(this).toggleClass("open");
  }),
  $("#PhoneNumber1").intlTelInput({
    initialCountry: "auto",
    autoPlaceholder: "aggressive",
    separateDialCode: !0,
    geoIpLookup: function (e) {
      $.get("https://ipinfo.io", function () {}, "jsonp").always(function (t) {
        var o = t && t.country ? t.country : "";
        e(o);
      });
    },
  }),
  $("#PhoneNumber1").on("countrychange", function (e, t) {
    $(this).val(""),
      $("#PhoneNumber2").val(""),
      $("#countryget").val($(this).intlTelInput("getSelectedCountryData").name),
      $("#code").val($(this).intlTelInput("getSelectedCountryData").dialCode);
  }),
  $("#PhoneNumbercon").intlTelInput({
    initialCountry: "auto",
    autoPlaceholder: "aggressive",
    separateDialCode: !0,
    geoIpLookup: function (e) {
      $.get("https://ipinfo.io", function () {}, "jsonp").always(function (t) {
        var o = t && t.country ? t.country : "";
        e(o);
      });
    },
  }),
  $("#PhoneNumbercon").on("countrychange", function (e, t) {
    $(this).val(""),
      $("#PhoneNumbercon").val(""),
      $("#countrygetcon").val(
        $(this).intlTelInput("getSelectedCountryData").name
      ),
      $("#codecon").val(
        $(this).intlTelInput("getSelectedCountryData").dialCode
      );
  });
