                    success:function(response){
                         if(response.status == 1){
                              var extension = response.extension;
                              var path = response.path;
                              
                              $('.prevel').hide();
                              if(extension == 'pdf' || extension == 'docx'){
                                    $("#fileprev").attr("href",path);
                                    $("#fileprev").show();
